INSERT INTO `semangatpagi` (`id`, `kalimat`, `oleh`, `tanggal`) VALUES
(1, 'Selamat pagi, kakk! Selamat pagi untukmu yang bangun dengan semangat baru. Hari ini adalah lembaran baru yang Allah berikan untukmu. Bismillah Semangat!', '1', '2023-08-17 09:23:05'),
(2, 'Selamat pagi, kakk! Semoga pagi ini membawa berkah dan kebahagiaan bagimu. Jadikan setiap langkahmu sebagai bentuk pengabdian kepada-Nya. Bismillah Semangat!', '1', '2023-08-17 09:23:05'),
(3, 'Selamat pagi, kakk! Bangunlah dengan hati penuh syukur atas nikmat-Nya yang tiada henti. Mari sambut pagi ini dengan senyuman dan semangat yang membara. Bismillah Semangat!', '1', '2023-08-17 09:23:05'),
(4, 'Selamat pagi, kakk! Pagi ini adalah kesempatan untuk meraih lebih banyak pahala. Jadikan setiap tindakanmu sebagai bentuk ibadah yang tulus. Bismillah Semangat!', '1', '2023-08-17 09:23:05'),
(5, 'Selamat pagi, kakk! Biarkan cahaya matahari mengingatkanmu tentang cahaya iman dalam hatimu. Dengan niat yang baik, kamu bisa menghadapi segala tantangan. Bismillah Semangat!', '1', '2023-08-17 09:23:05'),
(6, 'Selamat pagi, kakk! Ingatlah bahwa Allah selalu mendengar doa-doa dan keinginanmu. Percayalah pada-Nya, dan jadikan pagi ini sebagai langkah awal menuju kebahagiaan. Bismillah Semangat!', '1', '2023-08-17 09:23:05'),
(7, 'Selamat pagi, kakk! Biarkan setiap hembusan angin pagi mengingatkanmu tentang kehadiran-Nya yang selalu dekat. Dengan keyakinan dan usaha, kamu bisa mengatasi segala hal. Bismillah Semangat!', '1', '2023-08-17 09:23:05'),
(8, 'Selamat pagi, kakk! Jadikan pagi ini sebagai awal perjalananmu menuju kesuksesan dan keberkahan. Percayalah pada potensimu dan pada rencana Allah yang lebih baik. Bismillah Semangat!', '1', '2023-08-17 09:23:05'),
(9, 'Selamat pagi, kakk! Ingatlah bahwa Allah tidak akan pernah membiarkanmu sendirian dalam perjuangan ini. Berserahlah pada-Nya dan teruslah berusaha. Bismillah Semangat!', '1', '2023-08-17 09:23:05'),
